// src/components/Cart.js
import React, { useState, useEffect } from 'react';
import { getCart, removeFromCart, updateProductQuantity, clearCart } from '../utils/cartUtils';
import { useNavigate } from 'react-router-dom';
import '../styles/Cart.css';

const Cart = () => {
    const [cart, setCart] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        setCart(getCart());
    }, []);

    const handleRemoveFromCart = (productId) => {
        removeFromCart(productId);
        setCart(cart.filter(item => item.id !== productId));
    };

    const handleQuantityChange = (productId, quantity) => {
        updateProductQuantity(productId, quantity);
        setCart(cart.map(item => item.id === productId ? { ...item, quantity } : item));
    };

    const handleCheckout = () => {
        // Aquí podrías enviar los datos del carrito al backend para generar la factura
        clearCart();
        alert('Compra realizada con éxito!');
        navigate('/');
    };

    return (
        <div className="cart">
            <h2>Carrito de Compras</h2>
            {cart.length > 0 ? (
                cart.map(item => (
                    <div key={item.id} className="cart-item">
                        <img src={item.imageUrl} alt={item.name} className="cart-item-image" />
                        <div className="cart-item-details">
                            <h3>{item.name}</h3>
                            <p>${item.price}</p>
                            <input
                                type="number"
                                value={item.quantity}
                                onChange={(e) => handleQuantityChange(item.id, parseInt(e.target.value))}
                            />
                            <button onClick={() => handleRemoveFromCart(item.id)}>Eliminar</button>
                        </div>
                    </div>
                ))
            ) : (
                <p>Tu carrito está vacío.</p>
            )}
            {cart.length > 0 && <button onClick={handleCheckout} className="checkout-button">Finalizar Compra</button>}
        </div>
    );
};

export default Cart;
