import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '..styles/Header.css';

const Header = () => {
    const [user, setUser] = useState(null);

    useEffect(() => {
        // Fetch the user data from local storage or an API endpoint
        const loggedInUser = JSON.parse(localStorage.getItem('user'));
        if (loggedInUser) {
            setUser(loggedInUser);
        }
    }, []);

    const handleLogout = () => {
        // Clear user data from local storage and update state
        localStorage.removeItem('user');
        setUser(null);
    };

    return (
        <header className="header">
            <h1>Tienda Fabi</h1>
            <input type="text" placeholder="Buscar productos..." className="search-bar" />
            <div className="header-buttons">
                {user ? (
                    <>
                        <span>Hola, {user.firstName}</span>
                        <button onClick={handleLogout} className="logout-button">Logout</button>
                    </>
                ) : (
                    <Link to="/login" className="login-button">Iniciar Sesi√≥n</Link>
                )}
            </div>
        </header>
    );
};

export default Header;
