import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:8080/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            if (response.ok) {
                const data = await response.json();
                const userData = {
                    id: data.id, // Ensure your backend returns user ID
                    firstName: data.name,
                    token: data.accessToken,
                };
                localStorage.setItem('user', JSON.stringify(userData));
                navigate('/');
            } else {
                alert('Login failed');
            }
        } catch (error) {
            console.error('Error logging in:', error);
        }
    };

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit} className="login-form">
                <h2>Iniciar Sesión</h2>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="login-input"
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="login-input"
                />
                <button type="submit" className="login-button">Login</button>
                <div className="login-footer">
                    <button type="button" onClick={() => navigate('/signup')} className="footer-button">Regístrate</button>
                    <button type="button" onClick={() => alert('Feature not implemented yet')} className="footer-button">¿Olvidaste tu Contraseña?</button>
                </div>
            </form>
        </div>
    );
};

export default Login;
