// src/components/Product.js
import React from 'react';
import { addToCart } from '../utils/cartUtils';
import '../styles/Product.css';

const Product = ({ product, showAddToCartButton = true }) => {
    return (
        <div className="product-card">
            <img src={product.imageUrl} alt={product.name} className="product-image" onError={(e) => { e.target.onerror = null; e.target.src="https://via.placeholder.com/250"; }} />
            <h2 className="product-name">{product.name}</h2>
            <p>${product.price}</p>
            {showAddToCartButton && <button className="add-to-cart-button">Agregar al Carrito</button>}
        </div>
    );
}

export default Product;
